import "./style/Board.css";
import React, { useState } from "react";
import { Square } from "./Square";
import { useSelector } from "react-redux";

export default function Board() {
    // const count = useSelector((state) => state.count);
    const boardState = useSelector((state) => state.game);

    const boardComponent = [];

    for (let i = 0; i < boardState[0].length; i++) {
        let row = boardState[0][i];
        for (let j = 0; j < row.length; j++) {
            boardComponent.push(<Square symbol={boardState[0][i][j]} x={i} y={j}/>);
            // boardComponent.push((<Square symbol={boardState[i][j]} onClick={setBoard} boardState={boardState} x={i} y={j}/>))
        }
    }

    return (
        <div>
            {/* <h1>Count: {count}</h1> */}
            <div id="board">{boardComponent}</div>
        </div>
    );
}
