export default function clickReducer(
    state = 0, action
) {
    // if (action.type === 'somethingElse') {
    //     return state + 1;
    // }
    if (action.type === 'clickCount') {
        const value = state[action.x][action.y];
        if (value === 'on') {
            state -= 1;
        } else if (value === 'off'){
            state += 1;
        }
        return [...state];
    }
    return state;
}
